"use strict";

function sliderReviews() {
  var swiper = new Swiper('.reviews .swiper', {
    slidesPerView: '2',
    spaceBetween: 50,
    loop: false,
    breakpoints: {
      320: {
        slidesPerView: 1.7,
        spaceBetween: 15
      },
      992: {
        spaceBetween: 50
      }
    }
  });
}
function sliderEducation() {
  var swiper = new Swiper('.education .swiper', {
    slidesPerView: 'auto',
    spaceBetween: 45,
    loop: false,
    breakpoints: {
      320: {
        slidesPerView: 1.3,
        spaceBetween: 15
      },
      992: {
        spaceBetween: 50
      }
    }
  });
}
$(document).ready(function () {
  sliderReviews();
  sliderEducation();

  // Menu
  $(".header__menu-btn").click(function () {
    $(this).toggleClass("header__menu-btn--active");
    $(".header__nav").toggleClass("header__nav--active");
    $("body").toggleClass("fixed-body");
  });

  // Accordion
  $(".js-show").click(function () {
    $(this).toggleClass("js-show--active");
    $(this).siblings(".js-hidden").slideToggle();
    $(this).parents(".js").toggleClass('js--active');
  });

  // Scroll
  var header = $(".header");
  var scrollChange = 1;
  $(window).scroll(function () {
    var scroll = $(window).scrollTop();
    if (scroll >= scrollChange) {
      header.addClass("white-bg");
    } else {
      header.removeClass("white-bg");
    }
  });

  // Up
  var btn = $('.help__btn');
  $(window).scroll(function () {
    if ($(window).scrollTop() > 300) {
      btn.addClass('help__btn--active');
      $(window).scrollEnd(function () {
        btn.removeClass('help__btn--active');
      }, 3000);
    } else {
      btn.removeClass('help__btn--active');
    }
  });
  $.fn.scrollEnd = function (callback, timeout) {
    $(this).on('scroll', function () {
      var $this = $(this);
      if ($this.data('scrollTimeout')) {
        clearTimeout($this.data('scrollTimeout'));
      }
      $this.data('scrollTimeout', setTimeout(callback, timeout));
    });
  };
  btn.on('click', function (e) {
    e.preventDefault();
    $('html, body').animate({
      scrollTop: 0
    }, '300');
  });

  // Text-hidden
  $(".text-hidden").click(function () {
    $(".text-hidden").removeClass("text-hidden--active");
    $(this).addClass("text-hidden--active");
  });
  $(document).mouseup(function (e) {
    var div = $(".text-hidden");
    if (!div.is(e.target) && div.has(e.target).length === 0) {
      if ($(div).hasClass("text-hidden--active")) {
        $(div).removeClass("text-hidden--active");
      }
    }
  });
});
